import React, { useRef } from "react";

import "../../../style.css";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import iconWaiter from "../../../images/icon_waiter.png";
import DeleteButton from "../../../images/delete_staff_button.png";
import EditButton from "../../../images/edit_staff_button.png";
import axios from "axios";

import { useUserContext } from "../../../contexts/AuthContext";
import PreviousPageButton from "../../PreviousPageButton";
import { Button } from "react-bootstrap";
import { json, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";

export default function AllStaff() {
  const staffList = useRef([{}]);

  useEffect(async () => {
    if (staffList.current == [{}]) {
      const asyncFn = async () => {
        try {
          const response = await axios.get(`http://localhost:8081/staff/`);
          staffList.current = response.data; // Extrait la valeur du rôle depuis la réponse

          console.log(staffList.current);
        } catch (err) {
          console.log(err);
        }
      };
      asyncFn();
    }
  }, []);

  return (
    <div>
      <Row>
        <Col className="d-flex justify-content-center col-m-25" sm={12}>
          <h2 className="col-m-25">All Staff </h2>
        </Col>
        <Col className="">
          <Row className=" d-flex justify-content-center mx-auto">
            <Col className=" col-3 d-flex justify-content-center align-items-center">
              <img src={iconWaiter} alt="logo" className="circular-image-3" />
            </Col>
            <Col className=" customButton6   align-items-center">
              <Row>
                <Col className="col-10">
                  <Col className="col-12  h-50">
                    <span
                      style={{
                        fontSize: "16px",
                        fontFamily: "Satoshi Black",
                      }}
                    >
                      Merebe Abiguime
                      <span
                        style={{
                          fontSize: "12px",
                          color: "#757575",
                          fontFamily: "Satoshi Variable",
                        }}
                      >
                        (Chef)
                      </span>
                    </span>
                  </Col>
                  <Col className="  col-12">
                    <span>je m'appelle</span>
                  </Col>
                </Col>
                <Col className="col-2">
                  <img src={DeleteButton} alt="logo" className="" />
                  <img src={EditButton} alt="logo" className="" />
                </Col>
              </Row>
            </Col>
          </Row>
        </Col>
      </Row>
    </div>
  );
}
