import "../style.css";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import vector3 from "../images/Vector 3.png";
import vector4 from "../images/Vector 4.png";
import logo from "../images/logo.PNG";
import homepageIcon from "../images/homepage_icon.png";
import customerIcon from "../images/customer_icon.png";
import managerIcon from "../images/manager_icon.png";

import { Link } from "react-router-dom";
import { useUserContext } from "../contexts/AuthContext";
import { Container, Stack } from "react-bootstrap";

function SelectRole() {
  const { selectRole } = useUserContext();
  return (
    <>
      <Container className="gx-0 fluid ">
        <Stack>
          <div className="border ">
            <img src={vector3} alt="Vector 3" className="" />
            <img src={vector4} alt="Vector 4" className="" />
          </div>
          <div className="border mx-auto">
            <img src={logo} alt="logo" />
          </div>
          <div className="border mx-auto">
            <img src={homepageIcon} alt="logo" />
          </div>
          <div
            className="text-center"
            style={{ marginRight: "65px", marginLeft: "65px" }}
          >
            <h1 className="border">How would you like to register? </h1>
          </div>
          <Link
            to="/signup"
            style={{
              textDecoration: "none",
              color: "inherit",
              margin: "30px",
            }}
            onClick={() => selectRole(1)}
          >
            <Row className="border border-gray customButton4 ">
              <Col className=" col-6 border mx-auto text-center">
                <img src={customerIcon} alt="logo" />
              </Col>
              <Col className=" border col-6">
                <Col className="mx-auto align-items-center">
                  <h1 className="">Customer</h1>
                  <p className="customButton4-p">
                    Show your appreciation for good service by leaving a small
                    amount
                  </p>
                </Col>
              </Col>
            </Row>
          </Link>
        </Stack>
      </Container>
    </>
  );
}

export default SelectRole;
