import { Button, Container, Form, InputGroup, Stack } from "react-bootstrap";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import { Link } from "react-router-dom";

import IconStar from "../../../images/other_stars.png";
import IconStarUnselected from "../../../images/other_stars_unselected.png";
import Success from "../../../images/payment_success_icon.png";
import logo from "../../../images/logo.PNG";
import "../../../style.css";
import QRCode from "react-qr-code";
import StaffCaroussel from "./StaffCaroussel";
import { useState } from "react";
import { useStaffContext } from "../../../contexts/fetches-contexts/StaffContext";
import PaypalCheckoutButton from "../../../features/PaypalCheckoutButton";

function SuccessPayment() {
  const {
    setTipAmount,
    tipAmount,
    setTipComment,
    tipComment,
    rating,
    setRating,
  } = useStaffContext();

  const handleRatingClick = (clickedRating) => {
    setRating(clickedRating);
  };

  return (
    <Container className="gx-0 fluid ">
      <Stack>
        <div className=" mx-auto">
          <img key="star1" className="logo" src={logo} alt="logo" />
        </div>
        <div className=" mx-auto">
          <p className="payment-success">
            Your tip is greatly appreciated. I hope you had a lovley dining
            experience
          </p>
        </div>
        <div className=" mx-auto">
          <img className="logo" src={Success} alt="logo" />
        </div>
        <div className=" mx-auto">
          <p className="payment-success-amount">{`$ ${tipAmount}`} </p>
        </div>
        <div className=" mx-auto">
          <p className="payment-success-message">
            Your tip has been successfully sent
          </p>
        </div>

        <div className="d-flex justify-content-center mx-auto mt-4">
          <Button>Back to Home</Button>
        </div>
      </Stack>
    </Container>
  );
}

export default SuccessPayment;
