import React, { useState } from "react";
import IconCleaner from "../../../images/icon_cleaner.png";
import IconWaiter from "../../../images/icon_waiter.png";
import IconChef from "../../../images/icon_chef.png";
import { Carousel } from "react-bootstrap";
import { useStaffContext } from "../../../contexts/fetches-contexts/StaffContext";

export default function StaffCaroussel() {
  const [isImagesVisible, setImagesVisible] = useState(true);
  const { staffListFilter } = useStaffContext();

  const handleClick = () => {
    setImagesVisible(false);
  };

  return (
    <div>
      {isImagesVisible ? (
        <div className="mx-auto mb-4">
          <img
            style={{ width: "128px", height: "96px" }}
            src={IconChef}
            className="image_selectRole"
            alt="icon"
            onClick={handleClick}
          />
          <img
            style={{ width: "128px", height: "96px" }}
            src={IconCleaner}
            className="image_selectRole"
            alt="icon"
            onClick={handleClick}
          />
          <img
            style={{ width: "128px", height: "96px" }}
            src={IconWaiter}
            className="image_selectRole"
            alt="icon"
            onClick={handleClick}
          />
        </div>
      ) : (
        <div className="mx-auto mb-4">
          <Carousel>
            {staffListFilter.map((staff, index) => (
              <Carousel.Item>
                <img
                  style={{ width: "128px", height: "96px" }}
                  src={staff.pictureUrl}
                  className="image_selectRole"
                  alt="icon"
                  onClick={handleClick}
                />
                <Carousel.Caption>
                  <h3>{staff.firstName}</h3>
                  <p>
                    Nulla vitae elit libero, a pharetra augue mollis interdum.
                  </p>
                </Carousel.Caption>
              </Carousel.Item>
            ))()}
          </Carousel>
        </div>
      )}
    </div>
  );
}
