import "../../../style.css";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import vector3 from "../../../images/Vector 3.png";
import vector4 from "../../../images/Vector 4.png";
import logo from "../../../images/logo.PNG";
import homepageIcon from "../../../images/homepage_icon.png";
import customerIcon from "../../../images/customer_icon.png";
import managerIcon from "../../../images/manager_icon.png";

import { Link } from "react-router-dom";
import { useUserContext } from "../../../contexts/AuthContext";
import PreviousPageButton from "../../PreviousPageButton";

function SelectStaffRole() {
  const { selectRole } = useUserContext();
  return (
    <div>
      <Row>
        <Col className="previous-button" sm={12}>
          <PreviousPageButton />
        </Col>
        <Col className="justify-content-end" sm={12}>
          <div>
            <img src={vector3} alt="Vector 3" className="vector" />
          </div>
          <div>
            <img src={vector4} alt="Vector 4" className="vector" />
          </div>
        </Col>
        <Col
          className="d-flex justify-content-center  col-m-200
        "
          sm={12}
        >
          <h1 className="text-center">Hotel Details </h1>
        </Col>
        <Link
          to="/privateManager/private-home-worker/all-staff"
          style={{ textDecoration: "none", color: "inherit" }}
          onClick={() => selectRole(1)}
        >
          <Col
            className="d-flex border border-gray mx-auto align-items-center customButton4 col-button"
            sm={12}
          >
            <div className="mr-4 ">
              <img src={customerIcon} alt="logo" />
            </div>
            <div className="flex-grow-1  text-right d-flex justify-content-center align-items-center">
              <h1 className="mb-0">All Staff</h1>
            </div>
          </Col>
        </Link>
        <Link
          to="/privateManager/private-home-worker/add-staff"
          style={{ textDecoration: "none", color: "inherit" }}
          onClick={() => selectRole(1)}
        >
          <Col
            className="d-flex border border-gray mx-auto align-items-center customButton4 col-button"
            sm={12}
          >
            <div className="mr-4 ">
              <img src={managerIcon} alt="logo" />
            </div>
            <div className="flex-grow-1  text-right d-flex justify-content-center align-items-center">
              <h1 className="mb-0">Add Staff</h1>
            </div>
          </Col>
        </Link>
      </Row>
    </div>
  );
}

export default SelectStaffRole;
