import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import vector3 from "../images/Vector 3.png";
import vector4 from "../images/Vector 4.png";
import logo from "../images/logo.PNG";
import homepageIcon from "../images/homepage_icon.png";
import customerIcon from "../images/customer_icon.png";
import managerIcon from "../images/manager_icon.png";
import UploadImage from "../images/upload_image_signup.png";
import UserIcon from "../images/signup_user_icon.png";
import PhoneIcon from "../images/signup_phone_icon.png";
import MailIcon from "../images/signup_mail_icon.png";
import PasswordIcon from "../images/signup_password_icon.png";
import "../style.css";
import { Link } from "react-router-dom";
import React, { useState } from "react";
import { Button, InputGroup, Form } from "react-bootstrap";

const imageStyle = {
  position: "absolute",
  top: 0, // Ajustez la position verticale au besoin
  right: 0, // Positionner l'image tout à droite de l'écran
};
const customButton1 = {
  backgroundColor: "#FBBC04",
  fontFamily: "Satoshi Black",
  width: "100%",
  height: "65px",
  border: "0px",
  marginRight: "35px",
  marginLeft: "35px",
};
const customButton3 = {
  backgroundColor: "white",
  width: "323px",
  height: "65px",
  border: "0px",
};
const customForm = {
  backgroundColor: "white",
  width: "100%",
  height: "65px",
  border: "0px",
  borderRadius: "10px",
  color: "rgba(117, 117, 117, 1) ",
  backgroundColor: "rgba(217, 217, 217, 0.46)",
  marginBottom: "15px",
  fontSize: "14px",
  paddingLeft: "60px",
};
const icon = {
  position: "absolute",
  top: "25%",
  left: "1.25rem",
};
function SignUp() {
  const [currentStep, setCurrentStep] = useState(0);
  const [userRole, setUserRole] = useState(0);

  const handleRole = (role) => {
    setUserRole(role);
    setCurrentStep(currentStep + 1);
  };

  let content;
  if (currentStep === 0) {
    content = (
      <Row>
        <Col className="justify-content-end" sm={12}>
          <div>
            <img src={vector3} alt="Vector 3" style={imageStyle} />
          </div>
          <div>
            <img src={vector4} alt="Vector 4" style={imageStyle} />
          </div>
        </Col>
        <Col
          className=" d-flex justify-content-center"
          sm={12}
          style={{ marginTop: "200px" }}
        >
          <img src={logo} alt="logo" />
        </Col>
        <Col
          className=" d-flex justify-content-center mt-6"
          style={{ marginTop: "50px" }}
        >
          <img src={homepageIcon} alt="logo" />
        </Col>
        <Col
          className="d-flex justify-content-center mt-6"
          sm={12}
          style={{
            marginTop: "50px",
          }}
        >
          <h1 className="text-center">How would you like to register? </h1>
        </Col>
        <Link
          to="/signup"
          style={{ textDecoration: "none", color: "inherit" }}
          onClick={() => handleRole(1)}
        >
          <Col
            className="d-flex justify-content-center mt-6 border border-gray mx-auto align-items-center"
            sm={12}
            style={{
              marginTop: "50px",
              width: "80%",
              borderRadius: "10px",
              boxShadow: "1px 3px 5px 0px rgba(0,0,0,0.5)",
            }}
          >
            <div className="mr-4">
              {" "}
              {/* Ajoutez une classe de marge à droite pour l'image */}
              <img src={customerIcon} alt="logo" />
            </div>
            <div className="text-left">
              <h1>Customer</h1>
              <p>
                Show your appreciation for good service by leaving a small
                amount
              </p>
            </div>
          </Col>
        </Link>
        <Link
          to="/signup"
          style={{ textDecoration: "none", color: "inherit" }}
          onClick={() => handleRole(2)}
        >
          <Col
            className="d-flex justify-content-center mt-6 border border-gray mx-auto align-items-center"
            sm={12}
            style={{
              marginTop: "50px",
              marginBottom: "50px",
              width: "80%",
              borderRadius: "10px",
              boxShadow: "1px 3px 5px 0px rgba(0,0,0,0.5)",
            }}
          >
            <div className="mr-4">
              {" "}
              {/* Ajoutez une classe de marge à droite pour l'image */}
              <img src={managerIcon} alt="logo" />
            </div>
            <div className="text-left">
              <h1>Manager</h1>
              <p>
                Managing the hotel overall operation adding staff, check staff
                performance
              </p>
            </div>
          </Col>
        </Link>
      </Row>
    );
  } else if (currentStep === 1) {
    content = (
      <Row>
        <Col
          className="d-flex justify-content-center mt-6"
          sm={12}
          style={{
            marginTop: "50px",
          }}
        >
          <h1 className="">Create Account </h1>
        </Col>
        <Col
          className="d-flex justify-content-center mt-6"
          sm={12}
          style={{
            marginTop: "25px",
          }}
        >
          <p>
            Join the community with just a few taps. Enter the following
            information
          </p>
        </Col>
        <Col
          className=" d-flex justify-content-center mt-6"
          style={{ marginTop: "50px" }}
          sm={12}
        >
          <img src={UploadImage} alt="logo" />
        </Col>
        <Col
          className=" d-flex justify-content-center mt-6 "
          style={{ marginTop: "50px" }}
          sm={12}
        >
          <Form
            style={{ width: "100%", marginRight: "25px", marginLeft: "25px" }}
          >
            <InputGroup>
              <img style={icon} src={UserIcon} alt="User" />
              <Form.Control
                style={customForm}
                type="text"
                placeholder="First name"
              />
            </InputGroup>
            <InputGroup>
              <img style={icon} src={UserIcon} alt="User" />
              <Form.Control
                type="text"
                placeholder="Last name"
                style={customForm}
              />
            </InputGroup>
            <InputGroup>
              <img style={icon} src={MailIcon} alt="User" />
              <Form.Control
                type="email"
                placeholder="Email"
                style={customForm}
              />
            </InputGroup>
            <InputGroup>
              <img style={icon} src={PhoneIcon} alt="User" />
              <Form.Control
                type="text"
                placeholder="Phone number"
                style={customForm}
              />
            </InputGroup>
            <InputGroup>
              <img style={icon} src={PasswordIcon} alt="User" />
              <Form.Control
                type="password"
                placeholder="Password"
                style={customForm}
              />
            </InputGroup>
            <InputGroup>
              <img style={icon} src={PasswordIcon} alt="User" />
              <Form.Control
                type="password"
                placeholder="Confirm Password"
                style={customForm}
              />
            </InputGroup>
          </Form>
        </Col>
        <Col
          className="d-flex justify-content-center mt-6 "
          sm={12}
          style={{
            marginTop: "25px",
            marginBottom: "25px",
          }}
        >
          <Button style={customButton1}>Sign Up</Button>
        </Col>
      </Row>
    );
  }
  return <div>{content}</div>;
}

export default SignUp;
