import React from "react";
import QRCode from "react-qr-code";
import { useUserContext } from "../../../contexts/AuthContext";
import { Button, Col } from "react-bootstrap";

export default function PrivateHomeWorker() {
  const { userObject } = useUserContext();
  return (
    <div>
      <Row>
        <Col className="previous-button" sm={12}>
          <PreviousPageButton />
        </Col>
        <Col className="d-flex justify-content-center  col-m-200" sm={12}>
          <h1 className="col-m-25">Your tip QR </h1>
        </Col>
        <Col className="d-flex justify-content-center  col-m-50" sm={12}>
          <p>
            Share this QR Code with the hotel manager or they can scan it from
            your phone to add you as there employee
          </p>
        </Col>
        <Col className="d-flex justify-content-center  col-m-50" sm={12}>
          <QRCode
            size={256}
            style={{ height: "auto", maxWidth: "100%", width: "100%" }}
            value={`/privateManager/private-home-manager/worker-qrcode/${userObject.ID}`}
            viewBox={`0 0 256 256`}
          />
        </Col>
        <Col className="d-flex justify-content-center  col-m-200" sm={12}>
          <h1 className="col-m-25">
            {`${userObject.firstName} ${userObject.lastName}`}{" "}
          </h1>
        </Col>

        <Button type="submit" className="customButton1">
          Save to Gallery
        </Button>
        <Button type="submit" className="customButton1">
          Share this QR Code
        </Button>
      </Row>
    </div>
  );
}
