import React, { useContext, useState, createContext, useRef } from "react";
import { getAllStaff } from "../../fetches/FetchStaff";
import { getUser } from "../../fetches/FetchUsers";
import { useQuery } from "react-query";

export const StaffContext = createContext();

export function StaffContextProvider(props) {
  const staffObject = useRef({});
  const [staffListFilter, setStaffListFilter] = useState([{}]);
  const [staffList, setStaffList] = useState([{}]);

  const roleMap = ["Chef", "Waiter", "Cleaner"];

  const staffQuery = useQuery({
    queryKey: ["staff"],
    queryFn: () => getAllStaff(),
  });

  console.log();

  const newStaffList = [];
  const getStaffResponse = staffQuery.data;
  const userQuery = useQuery({
    queryKey: ["user"],
    queryFn: () => getUser(1),
  });
  console.log(userQuery.data);

  return (
    <StaffContext.Provider
      value={{
        staffObject,
        staffListFilter,
        setStaffListFilter,
        staffList,
        setStaffList,
      }}
    >
      {props.children}
    </StaffContext.Provider>
  );
}

export function useStaffContext() {
  const {
    staffObject,
    staffListFilter,
    setStaffListFilter,
    staffList,
    setStaffList,
  } = useContext(StaffContext);

  return {
    staffObject,
    staffListFilter,
    setStaffListFilter,
    staffList,
    setStaffList,
  };
}
